import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormControl,FormGroup ,Validators} from '@angular/forms';
import { RegisterService } from 'src/app/services/register.service';
import { BnNgIdleService } from 'bn-ng-idle'; 
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {
  repeatPass: string = 'none';
  constructor(private register:RegisterService,
              private bnIdle: BnNgIdleService,
              private router:Router) { }

  ngOnInit(): void {
 
    //60 = 1 minute
    this.bnIdle.startWatching(3600).subscribe((res) => {
      if (res) {
        console.log('session expired');
        // this.router.navigateByUrl('logout');
        this.router.navigate(["login"])
      }
    });

  }

  registerForm = new FormGroup({
    firstname: new FormControl("",[
      Validators.required,
      Validators.minLength(2),
      Validators.pattern("[a-zA-Z].*")]),
    lastname:new FormControl("",[
      Validators.required,
      Validators.minLength(2),
      Validators.pattern("[a-zA-Z].*")]),
    email:new FormControl("",[
      Validators.required,
      Validators.email]),
    mobile:new FormControl("",[
      Validators.required,
      Validators.maxLength(10),
      Validators.minLength(10),
      Validators.pattern("[0-9]*")]),
    gender: new FormControl("",[
      Validators.required]),
    pwd:new FormControl("",[
      Validators.required,
      Validators.maxLength(15),
      Validators.minLength(6),]),
    rpwd: new FormControl(""),
  });


  registersubmited(){
    if(this.Pwd.value == this.Rpwd.value){
      console.log(this.registerForm.valid);
      this.repeatPass = 'none'
      alert("sucussfully submited!");
      console.log(this.registerForm)
      this.register.saveregister(this.registerForm.value).subscribe((result)=>{
        console.log(result)
      })
      
    }else{
      this.repeatPass = 'inline'
      alert("submission failed !");
    }

    
  }

  get FirstName(): FormControl {
  return this.registerForm.get("firstname")as FormControl;
  }
  get LastName(): FormControl {
  return this.registerForm.get("lastname")as FormControl;
  }
  get Email(): FormControl {
  return this.registerForm.get("email")as FormControl;
  }
  get Mobile(): FormControl {
  return this.registerForm.get("mobile")as FormControl;
  }
  get Gender(): FormControl {
  return this.registerForm.get("gender")as FormControl;
  }
  get Pwd(): FormControl {
  return this.registerForm.get("pwd")as FormControl;
  }
  get Rpwd(): FormControl {
    return this.registerForm.get("rpwd")as FormControl;
  }


}
<form [formGroup]="registerForm" (ngSubmit)="registersubmited()"  class="border border-dark">
    <div class="row g-0" >
            <div class="col mb-5 text-center p-2  " style="border: lightblue;">
                <span class="fs-3">Register</span>
            </div>  
    </div> 
    <div class="row">
            <div class="col mb-3">
                <label for="firstname" class="form-label">First Name</label>
                <input type="text" class="form-control" id="firstname" formControlName="firstname"> 
                <div class="form-text text-danger" *ngIf="FirstName.errors?.['required'] && (FirstName.touched || FirstName.dirty)"> First Name is Required</div>
                <div class="form-text text-danger" *ngIf="FirstName.errors?.['minlength'] && (FirstName.touched || FirstName.dirty)">First Name is too short</div>
                <div class="form-text text-danger" *ngIf="FirstName.errors?.['pattern'] && (FirstName.touched || FirstName.dirty)">First Name should start with Alphabet</div>
            </div>
            <div class="col mb-3">
                <label for="lastname" class="form-label">Last Name</label>
                <input type="text" class="form-control" id="lastname" formControlName="lastname">
                <div class="form-text text-danger" *ngIf="!!LastName.errors?.['required'] && (LastName.touched || LastName.dirty)"> Last Name is Required</div>
                <div class="form-text text-danger" *ngIf="LastName.errors?.['minlength'] && (LastName.touched || LastName.dirty)">Last Name is too short</div>
                <div class="form-text text-danger" *ngIf="LastName.errors?.['pattern'] && (LastName.touched || LastName.dirty)">Last Name should start with Alphabet</div>
            </div>
    </div>
    <div class="row">
            <div class="col mb-3">
                <label for="email" class="form-control" >Email Adress</label>
                <input type="email" class="form-control" id="email" formControlName="email">
                <div class="form-text text-danger" *ngIf="Email.errors?.['required'] && (Email.dirty || Email.touched)"> 
                    Email is Required
                </div>
                <div class="form-text text-danger" *ngIf="Email.errors?.['email'] &&(Email.dirty || Email.touched)">
                    Email is Invalid
                </div>
            </div>
    </div>
    <div class="row">
        <div class="col mb-3">
                <label for="mobile" class="form-label">Mobile Number</label>
                <input type="text" class="form-control" id="mobile" formControlName="mobile">
                <div class="form-text text-danger" *ngIf="Mobile.errors?.['required'] && (Mobile.dirty || Mobile.touched)">Number is required</div>
                <div class="form-text text-danger" *ngIf="Mobile.errors?.['pattern']">Number Is Invalid</div>
                <div class="form-text text-danger" *ngIf="Mobile.errors?.['minlength']">Number Is Short</div>
                <div class="form-text text-danger" *ngIf="Mobile.errors?.['maxlength']">Number Is Long</div>
        </div>
        <div class="col ps-3 mb-3">
            <div class="m-1">Gender</div>
                <div class="form-check form-check-inline">
                    <input type="radio" class="form-check-input" value="m" id="male"formControlName="gender">
                    <label class="form-check-label" for="male">Male</label>
                </div>
                <div class="form-check form-check-inline">
                    <input type="radio" class="form-check-input" value="f" id="female"formControlName="gender">
                    <label class="form-check-label" for="female">Female</label>
                </div>
                <div class="form-text text-danger" *ngIf="Gender.errors?.['required']">Select Gender</div>
            </div>
    </div>
    <div class="row">
            <div class="col mb-3">
                <label for="pwd" class="form-label">Password</label>
                <input type="password" class="form-control" id="pwd" formControlName="pwd">
                <div class="form-text text-danger" *ngIf="Pwd.errors?.['required'] && (Pwd.dirty || Pwd.touched)">Password is required</div>
                <div class="form-text text-danger"*ngIf="Pwd.errors?.['minlength'] || Pwd.errors?.['maxlength'] && (Pwd.dirty || Pwd.touched)">Password should be at least 6 and at the max 15 characters long.</div>
            </div>
            <div class="col mb-3">
                <label for="rpwd" class="form-label">Repeat Password</label>
                <input type="password" class="form-control" id="rpwd"formControlName="rpwd">
                <div class="form-text text-danger" [ngStyle]="{'display':repeatPass}">Repeat password is Invalid.</div>
            </div>
    </div>
    <div class="row">
        <div class="col-6 offset-3">
            <div class="d-grid" style="margin-bottom: 2%;">
                <button type="submit" [disabled]="registerForm.invalid"  class="btn btn-primary">Submit</button>
            </div>
        </div>
    </div>

</form>
